var buy=document.querySelectorAll(".buy"),buyPopup=document.querySelector(".add-basket"),buyClose=document.querySelector(".close-add-basket");buy.forEach(function(e){e.addEventListener("click",function(e){e.preventDefault(),buyPopup.classList.add("window-block")})}),buyClose.addEventListener("click",function(e){e.preventDefault(),buyPopup.classList.remove("window-block")}),window.addEventListener("keydown",function(e){27===e.keyCode&&(e.preventDefault(),buyPopup.classList.contains("window-block")&&buyPopup.classList.remove("window-block"))})
